document.querySelectorAll("a").forEach(e=>{e.addEventListener("click",()=>{sessionStorage.setItem("lastClickedLink",e.getAttribute("href"))})}),window.addEventListener("load",()=>{let e=sessionStorage.getItem("lastClickedLink");if(e){let t=document.querySelector(`a[href="${e}"]`);t&&t.focus(),sessionStorage.removeItem("lastClickedLink")}});